import React, {useState} from 'react';
import './App.css';

function Card (num, suit){
  this.num = num;
  this.suit = suit;
}

const generateDeck = () => {
  const newDeck = new Set();
  const suits = ["♦", "♥", "♣", "♠"];
  for(const suit of suits){
    for(let i = 1; i <= 13; i++){
      newDeck.add(new Card(i, suit));
    }
  }
  return newDeck;
}

const drawCards = (num, deck) => {
  let arrDeck = Array.from(deck);
  let ranNums = new Set();
  while(ranNums.size < num){
    ranNums.push(Math.floor(Math.random(num) * arrDeck.length));
  }
}

function App() {

  const [deck, setDeck] = useState(generateDeck());
  const [inputNum, setInputNum] = useState("");

  const handleChange = (e) => {
    setInputNum(e.target.value);
  }

  const handleClick = (e) => {
    e.preventDefault();
    const curNum = +inputNum;
    if(curNum > deck.size){
      // error mesage - > we can't draw
    } else {
      // return array and new deck;
      drawCards(inputNum, deck);
    }
  }



  return (
    <div className="App">
      <h1>Deck of Cards</h1>
      <input onChange={handleChange} type="text" value={inputNum}/>
      <button onClick={handleClick}>Submit</button>
    </div>
  );
}

export default App;
