import React, {useState} from 'react';
import './App.css';

function Card (num, suit){
  this.num = num;
  this.suit = suit;
}

const generateDeck = () => {
  const newDeck = new Set();
  const suits = ["♦", "♥", "♣", "♠"];
  for(const suit of suits){
    for(let i = 1; i <= 13; i++){
      newDeck.add(new Card(i, suit));
    }
  }
  return newDeck;
}

const drawCards = (num, deck, selectedCards) => {
  let arrDeck = Array.from(deck);
  let ranNums = new Set();
  while(ranNums.size < num){
    ranNums.add(Math.floor(Math.random(num) * deck.size));
  }

  for(const ranNum of ranNums){
    selectedCards.push(arrDeck[ranNum]);
    deck.delete(arrDeck[ranNum]);
  }

  return [selectedCards, deck];
}

function App() {

  const [deck, setDeck] = useState(generateDeck());
  const [inputNum, setInputNum] = useState("");
  const [selected, setSelected] = useState([]);

  const handleChange = (e) => {
    setInputNum(e.target.value);
  }

  const handleClick = (e) => {
    e.preventDefault();
    const curNum = +inputNum;
    if(curNum > deck.size){
      // error mesage - > we can't draw
    } else {
      // return array and new deck;
      const [ newSelected, newDeck ]= drawCards(inputNum, deck, selected);
      setSelected(newSelected);
      setDeck(newDeck);
    }
  }

  console.log(deck);
  console.log(selected);

  return (
    <div className="App">
      <h1>Deck of Cards</h1>
      <input onChange={handleChange} type="text" value={inputNum}/>
      <button onClick={handleClick}>Submit</button>
      <div className="cards">
        {selected.map((card, idx) => <div className="card" key={idx}>{card.num} {card.suit}</div>)}
      </div>
    </div>
  );
}

export default App;
